<script type="text/javascript">
  jQuery(document).ready(function () {
var homepageChallengeAnswer = /^('|")[^'"]+\1;?$/;
var $javascriptInput = jQuery('.javascript-input input');
setTimeout(function(){
       jQuery('#inputJS').focus();
},600);

$javascriptInput.on("keyup", function(event){
   if (event.keyCode === 13){
      if ( $javascriptInput.val().match(homepageChallengeAnswer)){
           jQuery('.javascript-input input').addClass('correct');
           jQuery('.javascript-input--arrow').addClass('checkmark');
           jQuery('<a href="/try" class="button--next button--small">Next Challenge</a>').appendTo('.javascript-input');
           var name = $javascriptInput.val().replace(/['";]/g, "");
           document.cookie = "try_name=" + name
       } else {
            jQuery('.javascript-input input').removeClass('correct');
            jQuery('.javascript-input input').addClass('incorrect');
            jQuery('.javascript-input--arrow').removeClass('checkmark');
            jQuery('.button--next.button--small').remove();
            setTimeout(function(){ jQuery('.javascript-input input').removeClass('incorrect'); }, 600);
       }
   }
});
});
</script></div>
